import React, { useState } from 'react';
import { useHistory, Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import './AddEdit.css';

const AddEdit = () => {
  const history = useHistory();

  const initialState = {
    name: '',
    phone: '',
    school: '',
    class: '',
    rollNo: '',
    address: ''
  };

  const [state, setState] = useState(initialState);
  const { name, phone, school, class: classValue, rollNo, address } = state;

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setState({ ...state, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if(!name || !phone || !school || !classValue || !rollNo || !address){
        toast.error("Please provide value into feilds");
    }else{
        axios.post("http://localhost:5000/api/post",{
                name,
                phone,
                school,
                class: classValue,
                rollNo,
                address
            })
            .then(()=>{
                setState(initialState);
                toast.success("Added successfully.")
                setTimeout(() => history.push("/"), 500);
            })
            .catch((err) => toast.error(err.response.data));
    }
    
  };

  const downloadPDF = () => {
    axios.get("http://localhost:5000/api/pdf")
      .then((response) => {
        const blob = new Blob([response.data], { type: 'application/pdf' });
        const link = document.createElement('a');
        link.href = window.URL.createObjectURL(blob);
        link.download = 'students.pdf';
        link.click();
      })
      .catch((err) => console.log(err));
  };

  return (
    <div style={{ marginTop: '100px' }}>
      <form
        style={{
          margin: 'auto',
          padding: '15px',
          maxWidth: '400px',
          alignContent: 'center',
        }}
        onSubmit={handleSubmit}
        name='add-edit-form'
      >
        <label htmlFor='name'>Name</label>
        <input
          type='text'
          id='name'
          name='name'
          placeholder='Enter Your name'
          value={name}
          onChange={handleInputChange}
        />

<label htmlFor='phone'>Phone</label>
        <input
          type='text'
          id='name'
          name='name'
          placeholder='Enter Your name'
          value={name}
          onChange={handleInputChange}
        />


        <label htmlFor='school'>school</label>
        <input
          type='email'
          id='email'
          name='email'
          placeholder='Enter Your email'
          value={email}
          onChange={handleInputChange}
        />

        <label htmlFor='contact'>Class</label>
        <input
          type='tel'
          id='contact'
          name='contact'
          placeholder='Enter Your contact'
          value={contact}
          onChange={handleInputChange}
        />
        <label htmlFor='rollno'>Roll No</label>
        <input
          type='tel'
          id='contact'
          name='contact'
          placeholder='Enter Your contact'
          value={contact}
          onChange={handleInputChange}
        />
        <label htmlFor='address'>Address</label>
        <input
          type='tel'
          id='contact'
          name='contact'
          placeholder='Enter Your contact'
          value={contact}
          onChange={handleInputChange}
        />

        <input type='submit' value='Save' />

        <Link to='/'>
          <input type='button' value='Go Back' />
        </Link>
      </form>
    </div>
  );
};

export default AddEdit;
