import React, { useState } from 'react';
import { useHistory, Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import './AddEdit.css';

const AddEdit = () => {
  const history = useHistory();

  const initialState = {
    name: '',
    phone: '',
    school: '',
    class: '',
    rollNo: '',
    address: ''
  };

  const [state, setState] = useState(initialState);
  const { name, phone, school, class: classValue, rollNo, address } = state;

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setState({ ...state, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if(!name || !phone || !school || !classValue || !rollNo || !address){
        toast.error("Please provide value into feilds");
    }else{
        axios.post("http://localhost:5000/api/post",{
                name,
                phone,
                school,
                class: classValue,
                rollNo,
                address
            })
            .then(()=>{
                setState(initialState);
                toast.success("Added successfully.")
                setTimeout(() => history.push("/"), 500);
            })
            .catch((err) => toast.error(err.response.data));
    }
    
  };

  const downloadPDF = () => {
    axios.get("http://localhost:5000/api/pdf")
      .then((response) => {
        const blob = new Blob([response.data], { type: 'application/pdf' });
        const link = document.createElement('a');
        link.href = window.URL.createObjectURL(blob);
        link.download = 'students.pdf';
        link.click();
      })
      .catch((err) => console.log(err));
  };

  
};

export default AddEdit;
