import React, { useState } from 'react';
import { useHistory, Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import './AddEdit.css';

const AddEdit = () => {
    
    
    const initialState = {
        name: '',
        phone: '',
        school: '',
        myclass: '',
        rollNo: '',
        address: ''
    };
    const [state, setState] = useState(initialState);
    const { name, phone, school, myclass, rollNo, address } = state;
    const history = useHistory();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setState({ ...state, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if(!name || !phone || !school || !myclass || !rollNo || !address){
        toast.error('Please enter the details');
    }else {
        axios.post("http://localhost:5000/api/post", {
            name, 
            phone,
            school,
            myclass,
            rollNo,
            address,
        })
        .then(() =>{
            setState({name: "", phone: "", school: "", myclass: "", rollNo: "", address: "" });
        })
        .catch((err) => toast.error(err.response.data));
        setTimeout(() => history.push("/"), 500)
    }
  }
  
  

  const downloadPDF = () => {
    axios.get("http://localhost:5000/api/pdf")
      .then((response) => {
        const blob = new Blob([response.data], { type: 'application/pdf' });
        const link = document.createElement('a');
        link.href = window.URL.createObjectURL(blob);
        link.download = 'students.pdf';
        link.click();
      })
      .catch((err) => console.log(err));
  };

  return (
    <div style={{ marginTop: '100px' }}>
      <form
        style={{
          margin: 'auto',
          padding: '15px',
          maxWidth: '400px',
          alignContent: 'center',
        }}
        onSubmit={handleSubmit}
        name='add-edit-form'
      >
        <label htmlFor='name'>Name</label>
        <input
          type='text'
          id='name'
          name='name'
          placeholder='Enter Your name'
          value={name}
          onChange={handleInputChange}
        />

        <label htmlFor='phone'>Phone</label>
        <input
          type='text'
          id='phone'
          name='phone'
          placeholder='Enter Your phone'
          value={phone}
          onChange={handleInputChange}
        />

        <label htmlFor='school'>School</label>
        <input
          type='text'
          id='school'
          name='school'
          placeholder='Enter Your school'
          value={school}
          onChange={handleInputChange}
        />

        <label htmlFor='myclass'>myclass</label>
        <input
          type='text'
          id='myclass'
          name='myclass'
          placeholder='Enter Your myclass'
          value={myclass}
          onChange={handleInputChange}
        />

        <label htmlFor='rollNo'>Roll No</label>
        <input
          type='tel'
          id='rollNo'
          name='rollNo'
          placeholder='Enter Your roll no'
          value={rollNo}
          onChange={handleInputChange}
        />

        <label htmlFor='address'>Address</label>
        <input
          type='text'
          id='address'
          name='address'
          placeholder='Enter Your address'
          value={address}
          onChange={handleInputChange}
        />

        <input type='submit' value='Save' />

        <Link to='/'>
          <input type='button' value='Go Back' />
        </Link>
      </form>
    </div>
  );

};

export default AddEdit;
